import axios from 'axios';
export default {
  name: 'TheRegister',
  data() {
    return {
      userEmail: null,
      userName: null,
      password: null,
      backendMessage: null
    };
  },
  methods: {
    async handleSubmit() {
      let members = {
        userEmail: this.userEmail,
        userName: this.userName,
        password: this.password
      };
      try {
        const response = await axios.post(`http://localhost:3000/users`, members);
        this.backendMessage = response.data.messageregister;
      } catch (err) {
        console.log(err);
      }
    }
  }
};
